import {
  GET_MOVIES_ATTEMPT,
  GET_MOVIES_FAIL,
  GET_MOVIES_SUCCESS,

  GET_SIMILAR_MOVIES_ATTEMPT,
  GET_SIMILAR_MOVIES_FAIL,
  GET_SIMILAR_MOVIES_SUCCESS,

  ADD_TO_WATCHLIST,
  REMOVE_FROM_WATCHLIST,
  SET_ORDER_LIST
} from './constants';

import _ from 'lodash';

const initialState = {
  popular: {
    list: [],
    isLoading: false,
    errorMessage: null,
    direction: null
  },
  similar: {
    list: [],
    isLoading: false,
    errorMessage: null
  },
  watchList: {
    list: []
  }
};

const listing = (state = initialState, action) => {
  if (action && action.type) {
    switch (action.type) {
      case GET_MOVIES_ATTEMPT:
        return {
          ...state,
          popular: {
            isLoading: true
          }
        }
      case GET_MOVIES_FAIL:
        return {
          ...state,
          popular: {
            errorMessage: action.errorMessage,
            isLoading: false
          }
        }
      case GET_MOVIES_SUCCESS:
        return {
          ...state,
          popular: {
            list: action.payload.results.map(e => ({
              ...e,
              isAdded: false
            })),
            isLoading: false
          }
        }

      case GET_SIMILAR_MOVIES_ATTEMPT:
        return {
          ...state,
          similar: {
            isLoading: true
          }
        }
      case GET_SIMILAR_MOVIES_FAIL:
        return {
          ...state,
          similar: {
            errorMessage: action.errorMessage,
            isLoading: false
          }
        }
      case GET_SIMILAR_MOVIES_SUCCESS:
        return {
          ...state,
          similar: {
            list: action.payload.results,
            isLoading: false
          }
        }

      case ADD_TO_WATCHLIST:
        return {
          ...state,
          watchList: [...state.watchList, action.movie],
          popular: {
            list: [
              ...state.popular.list.map(m => m.id === action.movie.id ? action.movie : m)
            ]
          }
        }
      case REMOVE_FROM_WATCHLIST:
        return {
          ...state,
          watchList: _.reject(state.watchList, ['id', action.movie.id]),
          popular: {
            list: [
              ...state.popular.list.map(m => m.id === action.movie.id ? action.movie : m)
            ]
          }
        }

      case SET_ORDER_LIST:
        return {
          ...state,
          popular: {
            direction: action.direction,
            list: [
              ..._.orderBy(state.popular.list, ['release_date'], [`${action.direction}`])
            ]
          }
        }

      default: return state;
    }
  }
  return state;
};

export default listing;
