import { connect } from 'react-redux';
import Viewer from '../../Components/Viewer';
import { getPopularMovies as getPopularMoviesThunk } from '../../redux/index';

const mapStateToProps = state => ({
  isLoading: state.listing.isLoading
});

const mapDispatchToProps = dispatch => ({
  getPopularMovies: (input) => dispatch(getPopularMoviesThunk(input))
});

const mergeProps = (stateProps, dispatchProps, ownProps) => {
  const { getPopularMovies } = dispatchProps;
  return {
    ...stateProps,
    ...dispatchProps,
    ...ownProps,
    loadData: getPopularMovies
  }
};

export default connect(mapStateToProps, mapDispatchToProps, mergeProps)(Viewer);
