import React from 'react';
import { withStyles } from '@material-ui/core';

import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';

import FavoriteIcon from '@material-ui/icons/Favorite';
import FavoriteBorderOutlinedIcon from '@material-ui/icons/FavoriteBorderOutlined';

const Poster = (props) => {
  const {
    classes,
    id,
    poster_path,
    isAdded,
    isLiked,
    addToWatchList,
    removeFromWatchList
  } = props;

  return (
    <div className={classes.container}>
      <img
        alt={id}
        src={`http://image.tmdb.org/t/p/w154//${poster_path}`}
      />
      <div className={classes.iconContainer}>
        {/* {isLiked ?
          <FavoriteIcon onClick={() => console.log('Ajout à la liste des films likés')} color="error" />
          :<FavoriteBorderOutlinedIcon onClick={() => removeFromWatchList(id)} />
        } */}
        {isAdded ?
          <RemoveIcon onClick={() => removeFromWatchList(id)} />
          :<AddIcon onClick={() => addToWatchList({...props, isAdded: true})} />
        }
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flex: 1,
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center'
  },
  iconContainer: {
    display: 'flex',
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center'
  }
}

export default withStyles(styles)(Poster);
