import React, { useEffect, useState } from 'react';
import {
  AppBar, Toolbar, CircularProgress, Button,
  withStyles
} from '@material-ui/core';
import Poster from '../../Containers/Poster/index';
import MovieCard from '../../Containers/MovieCard/index';
import PopularList from '../../Containers/List/Popular/index';
import WatchList from '../../Containers/List/Watchlist/index';
import Watchlist from '../../Containers/List/Watchlist/index';

const Viewer = (props) => {
  const {
    classes,
    loadData,
    isLoading,
    errorMessage,
    popularMovies,
    watchList,
    setOrderList,
    popularListDirection,
    selectedMovie,
    selectMovie
  } = props;

  useEffect(() => { loadData() }, []);
  const [choosenList, setlist] = useState('popular');

  const newOrder = popularListDirection === 'asc' ? 'desc' : 'asc'

  return (
    <div className={classes.container}>
      <AppBar className={classes.tabContainer} position="sticky">
        <Toolbar className={classes.barContainer}>
          <div>
            <Button
              className={classes.buttonContainer}
              variant={choosenList === 'popular' ? "contained" : 'outlined'}
              color="secondary"
              onClick={() => setlist('popular')}
            >
              Tous les films
            </Button>
            <Button
              className={classes.buttonContainer}
              variant={choosenList === 'popular' ? "outlined" : 'contained'}
              color="secondary"
              onClick={() => setlist('myMovies')}
            >
              Ma liste
            </Button>
          </div>
          <div style={{color: 'white', fontSize: '40px', fontWeight: 'bold'}}>Welcome to MovieDB APP</div>
          <Button
            variant="contained"         
            color="secondary"
            onClick={() => setOrderList(newOrder)}
          >
            {popularListDirection === 'asc' ? 'Afficher les plus récents' : 'Afficher les plus anciens' }
          </Button>
        </Toolbar>
      </AppBar>
      {
        isLoading ? 
        <CircularProgress />
        :
        errorMessage ?
        <div>{errorMessage}</div>
        :
        <div className={classes.listContainer}>
          {
            choosenList === 'popular' ?
            <PopularList />
            : <Watchlist />
          }
        </div>
      }
      <MovieCard open={selectedMovie} onClose={() => selectMovie(null)}/>
    </div>
  )
};

const styles = {
  container: {
    backgroundColor: 'black',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'flex-start'
  },
  tabContainer: {
    paddingTop: 20,
    paddingBottom: 20,
    backgroundColor: 'black'
  },
  barContainer: {
    backgroundColor: 'black',
    justifyContent: 'space-between'
  },
  listContainer: {
    display: 'flex',
    flex: 1,
    flexDirection: 'row'
  },
  buttonContainer: {
    cursor: 'pointer',
    marginRight: 10
  }
}

export default withStyles(styles)(Viewer);
