import React, { useEffect, useState } from 'react';
import {
  AppBar, Toolbar, CircularProgress, Button,
  withStyles
} from '@material-ui/core';
import Poster from '../../Containers/Poster/index';

// const sortedArray = _.orderBy(popularMovies, ['release_date'], ['asc']);

const Viewer = (props) => {
  const {
    classes, loadData, isLoading, errorMessage, popularMovies, watchList
  } = props;

  useEffect(() => { loadData() }, []);
  const [choosenList, setlist] = useState('allMovies');

  const movieList = [];
  const myList = [];

  popularMovies.forEach(movie => {
    movieList.push(
      (<Poster {...movie}/>)
    )
  });

  watchList.forEach(movie => {
    myList.push(
      (<Poster {...movie}/>)
    )
  });

  const sortList = () => _.orderBy(movieList, ['release_date'], ['asc']);

  return (
    <div className={classes.container}>
      <AppBar position="sticky">
        <Toolbar>
          <Button
            className={classes.buttonContainer}
            variant={choosenList ? "contained" : 'outlined'}
            color="secondary"
            onClick={() => setlist('allMovies')}
          >
            Tous les films
          </Button>
          <Button
            className={classes.buttonContainer}
            variant={choosenList ? "outlined" : 'contained'}
            color="secondary"
            onClick={() => setlist('myMovies')}
          >
            Ma liste
          </Button>
        </Toolbar>
      </AppBar>
      <Button>
        Sort
      </Button>
      {
        isLoading ? 
        <CircularProgress />
        :
        errorMessage ?
        <div>{errorMessage}</div>
        :
        <div className={classes.listContainer}>
          {
            choosenList === 'allMovies' ?
            movieList
            : myList
          }
        </div>
      }
    </div>
  )
};

const styles = {
  container: {
    display: 'flex',
    flex: 1,
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center'
  },
  listContainer: {
    display: 'flex',
    flex: 1,
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center'
  },
  buttonContainer: {
    cursor: 'pointer'
  }
}

export default withStyles(styles)(Viewer);
