import React, { useState } from 'react';
import {
  Button, Drawer, Divider,
  withStyles
} from '@material-ui/core';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';

const MobileBar = ({ classes }) => {
  const [isOpen, setOpen] = useState(false);
  return (
    <div className={classes.container}>
      <div style={{color: '#f50057', fontSize: 20, fontWeight: 'bold', cursor: 'pointer'}} onClick={() => setOpen(!isOpen)}>
          MovieDB App
      </div>
      <Drawer
        variant="persistent"
        anchor="left"
        open={isOpen}
        classes={{
          paper: classes.drawerPaper
        }}
      >
        <ArrowBackIosIcon onClick={() => setOpen(false)} />
        <Divider />
        <Button
          // className={classes.buttonContainer}
          // variant={choosenList === 'popular' ? "contained" : 'outlined'}
          // color="secondary"
          // onClick={() => setlist('popular')}
        >
          <div className={classes.buttonText}>Tous les films</div>
        </Button>
        <Button
          // className={classes.buttonContainer}
          // variant={choosenList === 'popular' ? "outlined" : 'contained'}
          // color="secondary"
          // onClick={() => setlist('watchList')}
        >
          <div className={classes.buttonText}>Ma liste</div>
        </Button>
      </Drawer>
    </div>
  );
}

const styles = () => ({
  container: {
    margin: 20
  },
  drawerPaper: {
    width: 200,
    backgroundColor: 'grey'
  },
  buttonText: {
    fontWeight: 'bold'
  }
});

export default withStyles(styles)(MobileBar);
