import React, { useState } from 'react';
import { Dialog, CircularProgress, withStyles } from '@material-ui/core';
import Rating from '@material-ui/lab/Rating';
import CloseIcon from '@material-ui/icons/Close';
import FavoriteIcon from '@material-ui/icons/Favorite';
import moment from 'moment';

const MovieCard = (props) => {
  const {
    open,
    onClose,
    classes,
    id,
    original_title,
    overview,
    release_date,
    poster_path,
    isAdded,
    removeFromWatchList,
    addToWatchList,
    setSelectedMovieStatus,
    similarMovies,
    rating,
    setMovieRating,
    isLoading
  } = props;

  const posterPath = `http://image.tmdb.org/t/p/w342//${poster_path}`;

  const [value, setValue] = useState(0);

  const removeAndSet = () => {
    removeFromWatchList({...props, isAdded: false});
    setSelectedMovieStatus({...props, isAdded: false});
  };

  const addAndSet = () => {
    addToWatchList({...props, isAdded: true});
    setSelectedMovieStatus({...props, isAdded: true});
  };

  const similarList = [];

  similarMovies.forEach(movie => {
    similarList.push((
      <div>
        <img
          alt={movie.id}
          src={`http://image.tmdb.org/t/p/w92//${movie.poster_path}`}
        />
      </div>
    ))
  });

  return (
    <Dialog fullScreen className={classes.dialogContainer} open={open} onClose={onClose}>
      <div className={classes.container}>
        <CloseIcon color="secondary" fontSize="large" onClick={onClose} className={classes.cursor} />
        <img
          alt={id}
          src={posterPath}
        />
        <div className={classes.infoContainer}>
          <div className={classes.title}>{original_title}</div>
          <div className={classes.subtitle}>Sorti le {moment(release_date).format('DD/MM/YYYY')}</div>
          <div className={classes.corpus}>{overview}</div>
          <div className={classes.ratingContainer}>
            <FavoriteIcon
              className={classes.cursor}
              color={isAdded ? "error" : "inherit"}
              onClick={() => isAdded ? removeAndSet() : addAndSet()}
            />
            <Rating
              className={classes.cursor}
              name="simple-controlled"
              value={rating}
              onChange={(e, newValue) => {
                setMovieRating({
                  id,
                  original_title,
                  overview,
                  release_date,
                  poster_path,
                  isAdded,
                  rating: newValue
                });
              }}
            />
          </div>
          <div className={classes.similarContainer}>
            { isLoading ? <CircularProgress /> :similarList }
          </div>
        </div>
      </div>
    </Dialog>
  );
}

const styles = {
  container: {
    display: 'flex',
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center'
  },
  infoContainer: {
    margin: 20,
    width: 600,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center'
  },
  similarContainer: {
    display: 'flex',
    flexDirection: 'row',
  },
  ratingContainer: {
    display: 'flex',
    flexDirection: 'row',
    marginTop: 15,
    marginBottom: 15
  },
  title: {
    fontSize: 40,
    fontWeight: 'bold'
  },
  subtitle: {
    fontSize: 15,
    marginTop: 15,
    marginBottom: 15
  },
  corpus: {
    fontSize: 15,
    textAlign: 'justify'
  },
  cursor: {
    cursor: 'pointer'
  }
}

export default withStyles(styles)(MovieCard);
