import React, { useState } from 'react';
import {
  Button, Drawer,
  withStyles
} from '@material-ui/core';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';

const MobileBar = ({ classes }) => {
  const [isOpen, setOpen] = useState(false);
  return (
    <div className={classes.container}>
      <div style={{color: '#f50057', fontSize: 20, fontWeight: 'bold', cursor: 'pointer'}} onClick={() => setOpen(!isOpen)}>
          MovieDB App
      </div>
      <Drawer
        variant="persistent"
        anchor="left"
        open={isOpen}
        classes={{
          paper: classes.drawerPaper
        }}
      >
        <ArrowBackIosIcon />
      </Drawer>
    </div>
  );
}

const styles = () => ({
  container: {
    margin: 20
  },
  drawerPaper: {
    width: 200,
    backgroundColor: 'grey'
  }
});

export default withStyles(styles)(MobileBar);
