import React from 'react';
import _ from 'lodash';
import { withStyles } from '@material-ui/core';
import Row from '../Row/index';

const List = ({ classes, movieList }) => {
  return (
    <div>
      {_.isEmpty(movieList) ?
        <div className={classes.emptyListMessage}>
          Aucun film dans votre liste
        </div>
        :
        <div>
          <Row movies={movieList.slice(0,5)} />
          <Row movies={movieList.slice(5,10)} />
          <Row movies={movieList.slice(10,15)} />
          <Row movies={movieList.slice(15,20)} />
        </div>
      }
    </div>
  );
};

const styles = {
  emptyListMessage: {
    color: 'white',
    fontWeight: 'bold'
  }
};

export default withStyles(styles)(List);
