import React, { useEffect, useState } from 'react';
import {
  AppBar, Toolbar, CircularProgress, Button,
  withStyles
} from '@material-ui/core';
import Poster from '../../Containers/Poster/index';
import MovieCard from '../../Containers/MovieCard/index';

const Viewer = (props) => {
  const {
    classes,
    loadData,
    isLoading,
    errorMessage,
    popularMovies,
    watchList,
    setOrderList,
    popularListDirection,
    selectedMovie,
    selectMovie
  } = props;

  useEffect(() => { loadData() }, []);
  const [choosenList, setlist] = useState('popular');

  const movieList = [];
  const myList = [];

  popularMovies.forEach(movie => {
    movieList.push(
      (<Poster {...movie}/>)
    )
  });

  watchList.forEach(movie => {
    myList.push(
      (<Poster {...movie}/>)
    )
  });

  const newOrder = popularListDirection === 'asc' ? 'desc' : 'asc'

  return (
    <div className={classes.container}>
      <AppBar position="sticky">
        <Toolbar className={classes.barContainer}>
          <div>
            <Button
              className={classes.buttonContainer}
              variant={choosenList === 'popular' ? "contained" : 'outlined'}
              color="secondary"
              onClick={() => setlist('popular')}
            >
              Tous les films
            </Button>
            <Button
              className={classes.buttonContainer}
              variant={choosenList === 'popular' ? "outlined" : 'contained'}
              color="secondary"
              onClick={() => setlist('myMovies')}
            >
              Ma liste
            </Button>
          </div>
          <div style={{color: 'white', fontSize: '40px', fontWeight: 'bold'}}>MOVIE APP</div>
          <Button
            variant="contained"         
            color="secondary"
            onClick={() => setOrderList(newOrder)}
          >
            {popularListDirection === 'asc' ? 'Afficher les plus récents' : 'Afficher les plus anciens' }
          </Button>
        </Toolbar>
      </AppBar>
      {
        isLoading ? 
        <CircularProgress />
        :
        errorMessage ?
        <div>{errorMessage}</div>
        :
        <div className={classes.listContainer}>
          {
            choosenList === 'popular' ?
            movieList
            : myList
          }
        </div>
      }
      <MovieCard open={selectedMovie} onClose={() => selectMovie(null)}/>
    </div>
  )
};

const styles = {
  container: {
    backgroundColor: 'black',
    display: 'flex',
    flex: 1,
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center'
  },
  barContainer: {
    backgroundColor: 'black',
    justifyContent: 'space-between'
  },
  listContainer: {
    display: 'flex',
    flex: 1,
    width: '100%',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center'
  },
  buttonContainer: {
    cursor: 'pointer',
    marginRight: 10
  }
}

export default withStyles(styles)(Viewer);
