import { connect } from 'react-redux';
import Viewer from '../../Components/Viewer';
import {
  getPopularMovies as getPopularMoviesThunk,
  getSimilarMovies as getSimilarMoviesThunk
} from '../../redux/index';

const mapStateToProps = state => ({
  isLoading: state.listing.isLoading,
  errorMessage: state.listing.errorMessage
});

const mapDispatchToProps = dispatch => ({
  getPopularMovies: () => dispatch(getPopularMoviesThunk()),
  getSimilarMovies: (movieId) => dispatch(getSimilarMoviesThunk(movieId))
});

const mergeProps = (stateProps, dispatchProps, ownProps) => {
  const { getPopularMovies, getSimilarMovies } = dispatchProps;
  return {
    ...stateProps,
    ...dispatchProps,
    ...ownProps,
    loadData: () => {
      getPopularMovies();
      getSimilarMovies();
    }
  }
};

export default connect(mapStateToProps, mapDispatchToProps, mergeProps)(Viewer);
