import axios from 'axios';
import actions from './actions';

const apiKey = 'ac5f233e9b1ffda899bc1603606b1142';
const choosenLanguage = 'fr-FR';

// https://developers.themoviedb.org/3/movies/get-similar-movies 
// requête pour get des films similaires à celui sélectionné

// https://api.themoviedb.org/3/movie/popular?api_key=ac5f233e9b1ffda899bc1603606b1142&language=en-US&page=1

export const getPopularMovies = () => {
  return (dispatch) => {
    dispatch(actions.getMoviesAttempt());
    return axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&language=${choosenLanguage}&page=1`)
    .then((response) =>{
      const { data } = response;
      dispatch(actions.getMoviesSuccess(data.results));
      return response;
    })
    .catch((error) => {
      dispatch(actions.getMoviesFail(error));
      throw error;
    })
  } 
}
