import {
  GET_MOVIES_ATTEMPT,
  GET_MOVIES_FAIL,
  GET_MOVIES_SUCCESS
} from './constants';

const initialState = {
  popularMovies: [],
  errorMessage: null,
  isLoading: false
};

const listing = (state = initialState, action) => {
  if (action && action.type) {
    switch (action.type) {
      case GET_MOVIES_ATTEMPT:
        return {
          ...state,
          isLoading: true
        }
      case GET_MOVIES_FAIL:
        return {
          ...state,
          errorMessage: action.errorMessage,
          isLoading: false
        }
      case GET_MOVIES_SUCCESS:
        return {
          ...state,
          popularMovies: action.payload.results,
          isLoading: false
        }
      default: return state;
    }
  }
  return state;
};

export default listing;
