import axios from 'axios';
import actions from './actions';

const apiKey = 'ac5f233e9b1ffda899bc1603606b1142';
const choosenLanguage = 'fr-FR';

// https://developers.themoviedb.org/3/movies/get-similar-movies 
// requête pour get des films similaires à celui sélectionné

export const getPopularMovies = () => {
  return (dispatch) => {
    dispatch(actions.getMoviesAttempt());
    return axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&language=${choosenLanguage}&page=1`)
    .then((response) =>{
      const { data } = response;
      dispatch(actions.getMoviesSuccess(data));
      return response;
    })
    .catch((error) => {
      dispatch(actions.getMoviesFail(error.response.data.status_message));
      throw error;
    })
  } 
};

export const getSimilarMovies = (movieId = cfoeinv) => {
  return (dispatch) => {
    dispatch(actions.getSimilarMoviesAttempt());
    return axios.get(`https://api.themoviedb.org/3/movie/${movieId}/similar?api_key=${apiKey}&language=${choosenLanguage}&page=1`)
    .then((response) =>{
      const { data } = response;
      dispatch(actions.getSimilarMoviesSuccess(data));
      return response;
    })
    .catch((error) => {
      console.log({error});
      dispatch(actions.getSimilarMoviesFail());
      throw error;
    })
  } 
};
