import React, { useEffect } from 'react';
import {
  AppBar, Toolbar, CircularProgress, Button,
  withStyles
} from '@material-ui/core';
import Poster from '../Poster/index';

const Viewer = (props) => {
  const {
    classes, loadData, isLoading, errorMessage, popularMovies
  } = props;

  useEffect(() => { loadData() }, []);

  const movieList = [];

  popularMovies.forEach(movie => {
    movieList.push(
        (<Poster {...movie}/>)
    )
  });

  return (
    <div className={classes.container}>
      <AppBar position="sticky">
        <Toolbar>
          <div>Research bar</div>
        </Toolbar>
      </AppBar>
      {/* <div>
        <Button variant="outlined" color="primary">
          Du + r√©cent au plus + ancien
        </Button>
      </div> */}
      {
        isLoading ? 
        <CircularProgress />
        :
        errorMessage ?
        <div>{errorMessage}</div>
        :
        <div className={classes.listContainer}>
          {movieList}
        </div>
      }
    </div>
  )
};

const styles = {
  container: {
    width: 'auto',
    display: 'flex',
    flex: 1,
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center'
  },
  listContainer: {
    display: 'flex',
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center'

  }
}

export default withStyles(styles)(Viewer);
