import React, { useState } from 'react';
import {
  Button, Drawer,
  withStyles
} from '@material-ui/core';
import HighlightOffIcon from '@material-ui/icons/HighlightOff';

const MobileBar = ({ classes }) => {
  const [isOpen, setOpen] = useState(false);
  return (
    <div className={classes.container}>
      <div style={{color: '#f50057', fontSize: 20, fontWeight: 'bold', cursor: 'pointer'}} onClick={() => setOpen(!isOpen)}>
          MovieDB App
      </div>
      <Drawer
        variant="persistent"
        anchor="left"
        open={isOpen}
        classes={{
          paper: classes.drawerPaper
        }}
      >
        <HighlightOffIcon fontSize="large" color='secondary' onClick={() => setOpen(false)} />
        <div className={classes.buttonContainer}>
          <Button
            // variant={choosenList === 'popular' ? "contained" : 'outlined'}
            color="secondary"
            // onClick={() => setlist('popular')}
          >
            <div className={classes.buttonText}>Tous les films</div>
          </Button>
          <Button
            // variant={choosenList === 'popular' ? "outlined" : 'contained'}
            color="secondary"
            // onClick={() => setlist('watchList')}
          >
            <div className={classes.buttonText}>Ma liste</div>
          </Button>
        </div>
      </Drawer>
    </div>
  );
}

const styles = () => ({
  container: {
    margin: 20
  },
  drawerPaper: {
    width: 200,
    backgroundColor: '#373839',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    paddingTop: 40
  },
  buttonText: {
    fontWeight: 'bold',
    color: '#f50057'
  },
  buttonContainer: {
    marginTop: 20
  }
});

export default withStyles(styles)(MobileBar);
