import React from 'react';
import _ from 'lodash';
import { withStyles } from '@material-ui/core';
import Poster from '../../Containers/Poster/index';

const List = (props) => {
  const { classes, movieList, } = props;

  const list_0 = [];
  const list_1 = [];
  const list_2 = [];
  const list_3 = [];

  for (var i = 0; i<5; i++) {
      movieList[i] && list_0.push(<Poster {...movieList[i]} />)
  };

  for (var i = 5; i<10; i++) {
    movieList[i] && list_1.push(<Poster {...movieList[i]} />)
  };

  for (var i = 10; i<15; i++) {
    movieList[i] && list_2.push(<Poster {...movieList[i]} />)
  };

  for (var i = 15; i<20; i++) {
    movieList[i] && list_3.push(<Poster {...movieList[i]} />)
  };

  return (
    <div>
      {_.isEmpty(list_0) ?
        <div style={{color: 'red'}}>Aucun film dans votre liste</div>
        :
        <div>
          <div className= {classes.rowList}>
            {list_0}
          </div>
          <div className= {classes.rowList}>
            {list_1}
          </div>
          <div className= {classes.rowList}>
            {list_2}
          </div>
          <div className= {classes.rowList}>
            {list_3}
          </div>
        </div>
      }
    </div>
  );
};

const styles = {
  rowList: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center'
  },
  cursor: {
    cursor: 'pointer'
  }
};

export default withStyles(styles)(List);
