import React, { useEffect, useState } from 'react';
import {
  AppBar, Toolbar, CircularProgress, Button,
  withStyles
} from '@material-ui/core';
import Poster from '../../Containers/Poster/index';
import MovieCard from '../../Containers/MovieCard/index';

const Viewer = (props) => {
  const {
    classes,
    loadData,
    isLoading,
    errorMessage,
    popularMovies,
    watchList,
    setOrderList,
    popularListDirection,
    selectedMovie,
    selectMovie
  } = props;

  useEffect(() => { loadData() }, []);
  const [choosenList, setlist] = useState('popular');

  const movieList_0 = [];
  const movieList_1 = [];
  const movieList_2 = [];
  const movieList_3 = [];

  const myList_0 = [];
  const myList_1 = [];
  const myList_2 = [];
  const myList_3 = [];

  for (var i = 0; i<5; i++) {
    movieList_0.push(
      <Poster {...popularMovies[i]} />
    )
  }

  for (var i = 5; i<10; i++) {
    movieList_1.push(
      <Poster {...popularMovies[i]} />
    )
  }

  for (var i = 10; i<15; i++) {
    movieList_2.push(
      <Poster {...popularMovies[i]} />
    )
  }

  for (var i = 15; i<20; i++) {
    movieList_3.push(
      <Poster {...popularMovies[i]} />
    )
  }

  // popularMovies.forEach((movie, index) => {
  //   console.log(index);
  //   movieList_0.push(
  //     <Poster {...movie}/>
  //   )
  // });

  for (var i = 0; i<5; i++) {
    myList_0.push(
      <Poster {...watchList[i]} />
    )
  }

  for (var i = 5; i<10; i++) {
    myList_1.push(
      <Poster {...watchList[i]} />
    )
  }

  for (var i = 10; i<15; i++) {
    myList_2.push(
      <Poster {...watchList[i]} />
    )
  }

  for (var i = 15; i<20; i++) {
    myList_3.push(
      <Poster {...watchList[i]} />
    )
  }

  // watchList.forEach(movie => {
  //   myList.push(
  //     <Poster {...movie}/>
  //   )
  // });

  const newOrder = popularListDirection === 'asc' ? 'desc' : 'asc'

  return (
    <div className={classes.container}>
      <AppBar className={classes.tabContainer} position="sticky">
        <Toolbar className={classes.barContainer}>
          <div>
            <Button
              className={classes.buttonContainer}
              variant={choosenList === 'popular' ? "contained" : 'outlined'}
              color="secondary"
              onClick={() => setlist('popular')}
            >
              Tous les films
            </Button>
            <Button
              className={classes.buttonContainer}
              variant={choosenList === 'popular' ? "outlined" : 'contained'}
              color="secondary"
              onClick={() => setlist('myMovies')}
            >
              Ma liste
            </Button>
          </div>
          <div style={{color: 'white', fontSize: '40px', fontWeight: 'bold'}}>MOVIE APP</div>
          <Button
            variant="contained"         
            color="secondary"
            onClick={() => setOrderList(newOrder)}
          >
            {popularListDirection === 'asc' ? 'Afficher les plus récents' : 'Afficher les plus anciens' }
          </Button>
        </Toolbar>
      </AppBar>
      {
        isLoading ? 
        <CircularProgress />
        :
        errorMessage ?
        <div>{errorMessage}</div>
        :
        <div className={classes.listContainer}>
          {
            choosenList === 'popular' ?
            <div>
              <div style={{display: 'flex', flexDirection: 'row'}}>
                {movieList_0}
              </div>
              <div style={{display: 'flex', flexDirection: 'row'}}>
                {movieList_1}
              </div>
              <div style={{display: 'flex', flexDirection: 'row'}}>
                {movieList_2}
              </div>
              <div style={{display: 'flex', flexDirection: 'row'}}>
                {movieList_3}
              </div>
            </div>
          :
          <div>
            <div style={{display: 'flex', flexDirection: 'row'}}>
              {myList_0}
            </div>
            <div style={{display: 'flex', flexDirection: 'row'}}>
              {myList_1}
            </div>
            <div style={{display: 'flex', flexDirection: 'row'}}>
              {myList_2}
            </div>
            <div style={{display: 'flex', flexDirection: 'row'}}>
              {myList_3}
            </div>
          </div>
          }
        </div>
      }
      <MovieCard open={selectedMovie} onClose={() => selectMovie(null)}/>
    </div>
  )
};

const styles = {
  container: {
    backgroundColor: 'black',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'flex-start'
  },
  tabContainer: {
    paddingTop: 20,
    paddingBottom: 20,
    backgroundColor: 'black'
  },
  barContainer: {
    backgroundColor: 'black',
    justifyContent: 'space-between'
  },
  listContainer: {
    display: 'flex',
    flex: 1,
    flexDirection: 'row'
  },
  buttonContainer: {
    cursor: 'pointer',
    marginRight: 10
  }
}

export default withStyles(styles)(Viewer);
