import React from 'react';
import _ from 'lodash';
import { withStyles } from '@material-ui/core';
import Poster from '../../Containers/Poster/index';

const List = (props) => {
  const { classes, movieList } = props;

  const list_0 = [];
  const list_1 = [];
  const list_2 = [];
  const list_3 = [];

  for (var i = 0; i<5; i++) {
    movieList[i] && list_0.push(<Poster {...movieList[i]} />)
  };

  for (var j = 5; j<10; j++) {
    movieList[j] && list_1.push(<Poster {...movieList[j]} />)
  };

  for (var k = 10; k<15; k++) {
    movieList[k] && list_2.push(<Poster {...movieList[k]} />)
  };

  // for (var l = 15; l<20; l++) {
  //   movieList[l] && list_3.push(<Poster {...movieList[l]} />)
  // };

  return (
    <div>
      {_.isEmpty(list_0) ?
        <div className={classes.emptyListMessage}>Aucun film dans votre liste</div>
        :
        <div>
          <div className= {classes.rowList}>
            {list_0}
          </div>
          <div className= {classes.rowList}>
            {list_1}
          </div>
          <div className= {classes.rowList}>
            {list_2}
          </div>
          <div className= {classes.rowList}>
            {list_3}
          </div>
        </div>
      }
    </div>
  );
};

const styles = {
  rowList: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center'
  },
  cursor: {
    cursor: 'pointer'
  },
  emptyListMessage: {
    color: 'white',
    fontWeight: 'bold'
  }
};

export default withStyles(styles)(List);
