import React from 'react';
import {
  Drawer,
  withStyles
} from '@material-ui/core';
import HighlightOffIcon from '@material-ui/icons/HighlightOff';

const MobileCard = (props) => {
  const {
    classes, isOpen, onClose,
    id,
    original_title,
    overview,
    release_date,
    poster_path
  } = props;

  const posterPath = `http://image.tmdb.org/t/p/w185//${poster_path}`;

  return (
    <Drawer
      variant="persistent"
      anchor="left"
      open={isOpen}
      classes={{
        paper: classes.drawerPaper
      }}
    >
      <HighlightOffIcon fontSize="large" color='secondary' onClick={onClose} />
        <div className={classes.text}>{original_title}</div>
        <img
            alt={id}
            src={posterPath}
          />
        <div className={classes.text}>{release_date}</div>
        <div className={classes.text}>{overview}</div>
    </Drawer>
  );
}

const styles = () => ({
  drawerPaper: {
    width: '1OO%',
    backgroundColor: 'black',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: 30
  },
  text: {
    color: 'white'
  }
});

export default withStyles(styles)(MobileCard);
