import React, { useEffect, useState } from 'react';
import {
  AppBar, Toolbar, CircularProgress, Button,
  withStyles
} from '@material-ui/core';
import Poster from '../../Containers/Poster/index';

const Viewer = (props) => {
  const {
    classes, loadData, isLoading, errorMessage, popularMovies
  } = props;

  useEffect(() => { loadData() }, []);
  const [mainList, setMainlist] = useState(true);
  const [mylist, setMylist] = useState(false);

  const movieList = [];

  popularMovies.forEach(movie => {
    movieList.push(
      (<Poster {...movie}/>)
    )
  });

  return (
    <div className={classes.container}>
      <AppBar position="sticky">
        <Toolbar>
          <Button variant="contained" color="secondary">
            Tous les films
          </Button>
          <Button variant="contained" color="secondary">
            Ma liste
          </Button>
        </Toolbar>
      </AppBar>
      {
        isLoading ? 
        <CircularProgress />
        :
        errorMessage ?
        <div>{errorMessage}</div>
        :
        <div className={classes.listContainer}>
          {
            mainList ?
            movieList
            : null
          }
        </div>
      }
    </div>
  )
};

const styles = {
  container: {
    display: 'flex',
    flex: 1,
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center'
  },
  listContainer: {
    display: 'flex',
    flex: 1,
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center'

  }
}

export default withStyles(styles)(Viewer);
