import React, { useState } from 'react';
import { Dialog, withStyles } from '@material-ui/core';
import Rating from '@material-ui/lab/Rating';
import CloseIcon from '@material-ui/icons/Close';
import FavoriteIcon from '@material-ui/icons/Favorite';
import moment from 'moment';

const MovieCard = (props) => {
  const {
    open,
    onClose,
    classes,
    id,
    original_title,
    overview,
    release_date,
    poster_path,
    isAdded,
    removeFromWatchList,
    addToWatchList,
    setSelectedMovieStatus,
    similarMovies
  } = props;

  const posterPath = `http://image.tmdb.org/t/p/w342//${poster_path}`;

  const [value, setValue] = useState(0);

  const removeAndSet = () => {
    removeFromWatchList({...props, isAdded: false});
    setSelectedMovieStatus({...props, isAdded: false});
  };

  const addAndSet = () => {
    addToWatchList({...props, isAdded: true});
    setSelectedMovieStatus({...props, isAdded: true});
  };

  const similarList = [];

  for (var i = 0; i < 5; i++) {
    similarList.push(similarMovies[i])
  };

  const newList = similarList.forEach(movie => {
    similarLlsist.push((
      <div>
        <img
          alt={movie.id}
          src={`http://image.tmdb.org/t/p/w92//${movie.poster_path}`}
        />
      </div>
    ))
  });

  return (
    <Dialog fullScreen open={open} onClose={onClose}>
      <CloseIcon onClick={onClose} />
      <div className={classes.container}>
        <img
          alt={id}
          src={posterPath}
        />
        <div className={classes.infoContainer}>
          <div>{original_title}</div>
          <div>Sorti le {moment(release_date).format('DD/MM/YYYY')}</div>
          <div>{overview}</div>
          <FavoriteIcon
            color={isAdded ? "error" : "inherit"}
            onClick={() => isAdded ? removeAndSet() : addAndSet()}
          />
          <Rating
            name="simple-controlled"
            value={value}
            onChange={(event, newValue) => {
              console.log({newValue});
              setValue(newValue);
            }}
          />
          <div className={classes.similarContainer}>
            {newList}
          </div>
        </div>
      </div>
    </Dialog>
  );
}

const styles = {
  container: {
    display: 'flex',
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center'
  },
  infoContainer: {
    display: 'flex',
    flex: 1,
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center'
  },
  similarContainer: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center'
  }
}

export default withStyles(styles)(MovieCard);
