import React, { useState } from 'react';
import { Dialog, withStyles } from '@material-ui/core';
// import Rating from '@material-ui/lab/Rating';
import CloseIcon from '@material-ui/icons/Close';
import FavoriteIcon from '@material-ui/icons/Favorite';
import moment from 'moment';

const MovieCard = (props) => {
  const {
    open,
    onClose,
    classes,
    id,
    original_title,
    overview,
    release_date,
    poster_path,
    isAdded,
    removeFromWatchList,
    addToWatchList,
    setSelectedMovieStatus
  } = props;

  const posterPath = `http://image.tmdb.org/t/p/w342//${poster_path}`;

  // const [value, setValue] = useState(2);

  const removeAndSet = () => {
    removeFromWatchList({...props, isAdded: false});
    setSelectedMovieStatus({...props, isAdded: false});
  };

  const addAndSet = () => {
    addToWatchList({...props, isAdded: true});
    setSelectedMovieStatus({...props, isAdded: true});
  }

  return (
    <Dialog fullScreen open={open} onClose={onClose}>
      <CloseIcon onClick={onClose} />
      <div className={classes.container}>
        <img
          alt={id}
          src={posterPath}
        />
        <div className={classes.infoContainer}>
          <div>{original_title}</div>
          <div>{release_date}</div>
          <div>{overview}</div>
          <FavoriteIcon
            color={isAdded ? "error" : "inherit"}
            onClick={() => isAdded ? removeAndSet() : addAndSet()}
          />
          {/* <Rating
            name="simple-controlled"
            value={value}
            onChange={(event, newValue) => {
              setValue(newValue);
            }}
          /> */}
        </div>
      </div>
    </Dialog>
  );
}

const styles = {
  container: {
    display: 'flex',
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center'
  },
  infoContainer: {
    display: 'flex',
    flex: 1,
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center'
  }
}

export default withStyles(styles)(MovieCard);
