import React, { useEffect, useState } from 'react';
import {
  AppBar, Toolbar, CircularProgress, Button, withWidth,
  withStyles
} from '@material-ui/core';

import List from '../List/index';
import MobileBar from '../MobileBar/index';
import MovieCard from '../../Containers/MovieCard/index';
import MobileCard from '../../Containers/MobileCard/index';
import MenuIcon from '@material-ui/icons/Menu';

const Viewer = (props) => {
  const {
    classes,
    width,

    loadData,
    isLoading,
    errorMessage,

    setOrderList,
    selectedList,
    popularListDirection,
    watchListDirection,

    selectedMovie,
    resetSelectedMovie,

    popularMovies,
    watchList,
    setList

  } = props;

  useEffect(() => { loadData() }, []); //eslint-disable-line
  // const [choosenList, setlist] = useState('popular');

  const newOrderForPopular = popularListDirection === 'asc' ? 'desc' : 'asc';
  const newOrderForWatchlist = watchListDirection === 'asc' ? 'desc' : 'asc';

  return (
    <div className={classes.container}>
    {width === 'xs' ?
      <MobileBar
        firstButton={() => setlist('popular')}
        secondButton={() => setlist('watchList')}
      />
      :      
      <AppBar className={classes.tabContainer} position="sticky">
        <Toolbar className={classes.barContainer}>
          <div>
            <Button
              className={classes.buttonContainer}
              variant={selectedList === 'popular' ? "contained" : 'outlined'}
              color="secondary"
              onClick={() => setlist('popular')}
            >
              <div className={classes.buttonText}>Tous les films</div>
            </Button>
            <Button
              className={classes.buttonContainer}
              variant={selectedList === 'popular' ? "outlined" : 'contained'}
              color="secondary"
              onClick={() => setlist('watchList')}
            >
              <div className={classes.buttonText}>Ma liste</div>
            </Button>
          </div>
          <div className={classes.titleStyle}>MovieDB App</div>
          <Button
            variant="contained"         
            color="secondary"
            onClick={() => setOrderList(selectedList, selectedList === 'popular' ? newOrderForPopular : newOrderForWatchlist)}
          >
            <div className={classes.buttonText}>
              { popularListDirection === 'asc' ?
                'Afficher les plus récents'
                : 'Afficher les plus anciens' }
            </div>
          </Button>
        </Toolbar>
      </AppBar>
    }
      {
        errorMessage &&
        <div className={classes.errorMessageStyle}>{errorMessage}</div>
      }
      {
        isLoading ? <CircularProgress /> :
        <div className={classes.listContainer}>
          { selectedList === 'popular' ? 
          <List movieList={popularMovies} />
          : <List movieList={watchList} /> }
        </div>
      }
      {width === 'xs' ?
      <MobileCard isOpen={selectedMovie} onClose={() => resetSelectedMovie(null)}/>
      :<MovieCard open={selectedMovie} onClose={() => resetSelectedMovie(null)}/>
      }
      {width === 'xs' &&
        <div className={classes.menuButton} >
          <MenuIcon color='inherit' fontSize='large' />
        </div>
      }
    </div>
  )
};

const styles = () => ({
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'flex-start'
  },
  tabContainer: {
    paddingTop: 20,
    paddingBottom: 20,
    backgroundColor: 'black'
  },
  titleStyle: {
    color: '#f50057',
    fontSize: 40,
    fontWeight: 'bold'
  },
  barContainer: {
    backgroundColor: 'black',
    justifyContent: 'space-between'
  },
  buttonText: {
    fontWeight: 'bold'
  },
  listContainer: {
    display: 'flex',
    flex: 1,
    flexDirection: 'row'
  },
  buttonContainer: {
    cursor: 'pointer',
    marginRight: 10
  },
  errorMessageStyle: {
    color: 'red',
    fontWeight: 'bold',
    fontSize: 30
  },
  drawerPaper: {
    width: 200
  },
  menuButton: {
    position: 'absolute',
    bottom: 30,
    left: 30,
    borderRadius: '50%',
    backgroundColor: '#f50057',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: 5
  }
});

export default withWidth()(withStyles(styles)(Viewer));
