import React from 'react';
import { Dialog, withStyles } from '@material-ui/core';

const MovieCard = (props) => {
  const {
    classes,
    open,
    poster_path='/tWjJ3ILjsbTwKgXxEv48QAbYZ19.jpg'
  } = props;

  const posterPath = `http://image.tmdb.org/t/p/w342//${poster_path}`;

  return (
    <Dialog fullScreen className={classes.container} open={open}>
      <div>
        <img
          alt={'coco'}
          src={posterPath}
        />
        <div>title</div>
      </div>
    </Dialog>
  );
}

const styles = {
  container: {
    display: 'flex',
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center'
  }
}

export default withStyles(styles)(MovieCard);
