import React, { useState } from 'react';
import {
  Button, Drawer,
  withStyles
} from '@material-ui/core';
import HighlightOffIcon from '@material-ui/icons/HighlightOff';

const MobileBar = ({ classes, firstButton, secondButton }) => {
  const [isOpen, setOpen] = useState(false);
  return (
    <div className={classes.container}>
      <div className={classes.title} onClick={() => setOpen(!isOpen)}>
          MovieDB App
      </div>
      <Drawer
        variant="persistent"
        anchor="left"
        open={isOpen}
        classes={{
          paper: classes.drawerPaper
        }}
      >
        <HighlightOffIcon fontSize="large" color='secondary' onClick={() => setOpen(false)} />
        <div className={classes.buttonContainer}>
          <Button
            color="secondary"
            onClick={firstButton}
          >
            <div className={classes.buttonText}>Tous les films</div>
          </Button>
          <Button
            color="secondary"
            onClick={secondButton}
          >
            <div className={classes.buttonText}>Ma liste</div>
          </Button>
        </div>
      </Drawer>
    </div>
  );
}

const styles = () => ({
  container: {
    margin: 20
  },
  title: {
    color: '#f50057',
    fontSize: 20,
    fontWeight: 'bold'
  },
  drawerPaper: {
    width: 150,
    backgroundColor: '#373839',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    paddingTop: 30
  },
  buttonText: {
    fontWeight: 'bold',
    color: '#f50057'
  },
  buttonContainer: {
    marginTop: 20
  }
});

export default withStyles(styles)(MobileBar);
