import React from 'react';
import Poster from '../../Containers/Poster/index';
import { withStyles } from '@material-ui/core';

const Row = (props) => {
  const { classes, movies } = props;
  const list = [];

  // for (var i = 0; i<5; i++) {
  //   movies[i] && list.push(<Poster {...movies[i]} />)
  // };

  movies.forEach(m => {
    m && list.push(<Poster {...m} />)
  });

  return (
    <div className= {classes.container}>
      {list}
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center'
  }
};

export default withStyles(styles)(Row);
