import React from 'react';
import { withStyles } from '@material-ui/core';
import AddIcon from '@material-ui/icons/Add';
import FavoriteIcon from '@material-ui/icons/Favorite';

const Poster = (props) => {
  const {
    classes,
    id,
    poster_path
  } = props;

  return (
    <div className={classes.container}>
      <img
        alt={id}
        src={`http://image.tmdb.org/t/p/w154//${poster_path}`}
      />
      <div className={classes.iconContainer}>
        <FavoriteIcon onClick={() => console.log('Ajout à la liste des films likés')} color="error" />
        <AddIcon onClick={() => console.log('Ajout à la liste des films à voir')} />
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flex: 1,
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center'
  },
  iconContainer: {
    display: 'flex',
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center'
  }
}

export default withStyles(styles)(Poster);
